import styles from "./Home.module.css";
import { useEffect, useState } from "react";
import { BASE } from "../../../api";
import { toast } from "react-toastify";
import { useNavigate } from "react-router-dom";
import { useUser } from "../../GlobalUserContext";
import Card from './Card';

const Home = () => {
  const [cards, setCards] = useState([
    {
      _id:"69008f218ed2e2b53cfe8e1e",
      name: "Suii",
      age: "20",
      location: "IDK",
      tripType: ["Business", "Budget"],
      destination: "Mumbai",
      groupSize: "3",
      activities: ["City Tours", "Nightlife", "Photography"],
      contact: "12345689",
      totalCost: "15000",
      travelDate: "12/12/25",
      budget: "5000",
      notes: "Nothing",
      photo:"data:image/jpeg;base64,"
    },
  ]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const { state, dispatch } = useUser();

  const fetchCards = async () => {
    try {
      const res = await fetch(`${BASE}/recommendations`, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          Accept: "application/json",
          Authorization: `Bearer ${localStorage.getItem("userToken")}`,
        },
      });

      if (res.ok) {
        const data = await res.json();
        console.log("Fetched cards:", data);
        setCards(data);
      } else if (res.status === 403) {
        toast.error("Token Invalid Login Again");
        setTimeout(() => {
          sessionStorage.removeItem("userToken");
          dispatch({ type: "CLEAR_USER" });
          navigate("/login", { replace: true });
        }, 3000);
      } else {
        toast.error("Someting went wrong");
      }
    } catch (error) {
      console.error("Error fetching user data:", error);
      toast.error("Network error, please try again later.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchCards();
  }, []);


  async function handleLike(id) {
    try {
      const res = await fetch(`${BASE}/likeTrip`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Accept: "application/json",
          Authorization: `Bearer ${localStorage.getItem("userToken")}`,
        },
        body: JSON.stringify({ tripId:id }),
      });

      if (res.ok) {
        const data = await res.json();
        console.log("Fetched cards:", data);
      } else if (res.status === 403) {
        toast.error("Token Invalid Login Again");
        setTimeout(() => {
          sessionStorage.removeItem("userToken");
          dispatch({ type: "CLEAR_USER" });
          navigate("/login", { replace: true });
        }, 3000);
      } else {
        toast.error("Someting went wrong");
      }
    } catch (error) {
      console.error("Error fetching user data:", error);
      toast.error("Network error, please try again later.");
    }
  }

  const handleSwipe = (card) => {
    setCards((prev) => prev.filter((e) => e._id !== card._id));
  };

  if (loading) {
    return <div className={styles.loading}>Loading cards...</div>;
  }

  return (
    <div className={styles.homeContainer}>
      {cards.map((card, index) => (
        <Card
          key={index}
          card={card}
          onSwipe={handleSwipe}
          handleLike={handleLike}
        />
      ))}
    </div>
  );
};

export default Home;
